import '@/style.css'
import App from '@/app.vue'
import router from '@/router.js'

var oldDOM = document.querySelector('#app-attach-point');
var newDOM = document.createElement('div');
newDOM.id = '#app-attach-point';
document.body.appendChild(newDOM);
var target = oldDOM || newDOM;

var app = new Vue({
  router,
  el: target,
  components: {
    App
  },
  template: '<div id="app"><App /></div>'
})

window.addEventListener('message', (evt) => {
  var { type, data } = evt.data;
  window.dispatchEvent(new CustomEvent(type, { detail: data }))
}, false);

window.addEventListener('slider', ({ detail }) => {
  console.log(detail);
  (window.opener || window.top).dispatchEvent(new CustomEvent('loklok', { detail: { rand: Math.random() } }))
});

export default app;

import Player from '@/webgl/layout/Player.vue'
import Home from '@/webgl/pages/Home.vue'
import Happy from '@/webgl/pages/Happy.vue'

const router = new VueRouter({
  mode: 'hash',
  routes: [
    {
      path: '/',
      component: Player,
      children: [
        {
          path: '',
          component: Home
        },
        {
          path: 'happy',
          component: Happy
        }
      ]
    }
  ]
})

export default router

