@import url('https://fonts.googleapis.com/css?family=Quicksand');

html, body {
  width: 100%;
  height: 100%;
  margin: 0px;
  padding: 0px;
}
#app {
  box-sizing: border-box;
  width: 100%;
  height: 100%;
  overflow: hidden;
  font-family: 'Quicksand', sans-serif;
  background-color: white;
  position: relative;
}
.full{
  width: 100%;
  height: 100%;
}

import '@/style.css'
import App from '@/app.vue'
import router from '@/router.js'

var vpDOM = document.querySelector('#app-vp');
var oldDOM = document.querySelector('#app-attach-point');
var newDOM = document.createElement('div');
newDOM.id = 'app';

var target = vpDOM || oldDOM || newDOM;

if (!oldDOM && !vpDOM) {
  document.body.appendChild(newDOM);
}

var app = new Vue({
  router,
  el: target,
  data: {
    vp: vpDOM
  },
  components: {
    App
  },
  methods: {
    getStyle () {
      if (vpDOM) {
        return {
          width: '100vw',
          height: '100vh'
        }
      } else {
        return {}
      }
    }
  },
  template: '<div id="app" :style="getStyle()"><App /></div>'
});


window.addEventListener('main-system-ready', (evt) => {
  var data = evt.detail
  console.log('App Got Custom Event:', data)
}, false);

export default app;

import Player from '@/layout/Player.vue'
import Home from '@/pages/Home.vue'
import Home2 from '@/pages/Home2.vue'
import Happy from '@/pages/Happy.vue'
import PALMS from '@/pages/PALMS.vue'
import OM3D from '@/pages/OM3D.vue'
import YO2 from '@/pages/YO2.vue'
import Sample from '@/pages/Sample.vue'

const router = new VueRouter({
  mode: 'hash',
  routes: [
    {
      path: '/',
      component: Home2
    },
    {
      path: '/om3d',
      component: OM3D
    },
    {
      path: '/yo2',
      component: YO2
    },

    {
      path: '/sample',
      component: Sample
    },
    {
      path: '/v1',
      component: Player,
      children: [
        {
          path: '',
          component: Happy
        }
      ]
    }
  ]
})

export default router

